@@ -0,0 +1,56 @@
+import { useEffect, useState } from "react";
+import Footer from "./components/Footer";
+import SideBar from "./components/SideBar";
+import Main from "./components/Main";
+
+function App() {
+  const [showModal, setShowModal] = useState(false);
+  const [channelData, setChannelData] = useState(null);
+  const [error, setError] = useState(null);
+
+  function handleToggleModal() {
+    setShowModal(!showModal);
+  }
+
+  useEffect(() => {
+    async function fetchChannelData() {
+      try {
+        const apiKey = import.meta.env.VITE_YOUTUBE_API_KEY;
+        const channelId = "UCY5a3u7r2lkN5-ZNj8DCtQA"; // Replace with the actual channel ID
+        const url = `https://www.googleapis.com/youtube/v3/channels?part=snippet,contentDetails,statistics&id=${channelId}&key=${apiKey}`;
+
+        const response = await fetch(url);
+        if (!response.ok) {
+          throw new Error('Network response was not ok');
+        }
+
+        const data = await response.json();
+        setChannelData(data);
+      } catch (error) {
+        setError(error.message);
+      }
+    }
+
+    fetchChannelData();
+  }, []);
+
+  return (
+    <>
+      <Main />
+      {showModal && <SideBar handleToggleModal={handleToggleModal} />}
+      <Footer handleToggleModal={handleToggleModal} />
+      {error && <p>Error: {error}</p>}
+      {channelData && channelData.items.length > 0 && (
+        <div>
+          <h1>{channelData.items[0].snippet.title}</h1>
+          <p>{channelData.items[0].snippet.description}</p>
+          <p>Subscribers: {channelData.items[0].statistics.subscriberCount}</p>
+          <p>Views: {channelData.items[0].statistics.viewCount}</p>
+          <p>Videos: {channelData.items[0].statistics.videoCount}</p>
+        </div>
+      )}
+    </>
+  );
+}
+
+export default App;

