+import { useEffect, useState } from "react";
+import Footer from "./components/Footer";
+import SideBar from "./components/SideBar";
+import Main from "./components/Main";
+
+function App() {
+  const [showModal, setShowModal] = useState(false);
+  const [pageContent, setPageContent] = useState("");
+  const [error, setError] = useState(null);
+  const [isLoading, setIsLoading] = useState(true); // Track loading state
+
+  function handleToggleModal() {
+    setShowModal(!showModal);
+  }
+
+  useEffect(() => {
+    async function fetchAPIData() {
+      try {
+        const apiKey = import.meta.env.VITE_YOUTUBE_API_KEY;
+
+        if (!apiKey) {
+          throw new Error("API Key is not defined");
+        }
+
+        // Construct the YouTube API URL correctly
+        const url = `https://www.googleapis.com/youtube/v3/channels?part=snippet,contentDetails,statistics&forUsername=BMW&key=${apiKey}`;
+
+        const response = await fetch(url);
+        if (!response.ok) {
+          throw new Error('Network response was not ok');
+        }
+
+        const data = await response.json();
+        console.log(data); // Log the response to understand its structure
+
+        // Check if items exist and handle the API response
+        if (data && data.items && data.items.length > 0) {
+          const channelInfo = data.items[0];
+          setPageContent(`
+            <h1>${channelInfo.snippet.title}</h1>
+            <p>${channelInfo.snippet.description}</p>
+            <p>Subscribers: ${channelInfo.statistics.subscriberCount}</p>
+            <p>Views: ${channelInfo.statistics.viewCount}</p>
+            <p>Videos: ${channelInfo.statistics.videoCount}</p>
+          `);
+        } else {
+          setPageContent("<p>No data available.</p>");
+        }
+      } catch (error) {
+        console.error('Fetch error:', error);
+        setError(error.message);
+      } finally {
+        setIsLoading(false); // Set loading to false after data is fetched or an error occurs
+      }
+    }
+
+    fetchAPIData();
+  }, []);
+
+  return (
+    <>
+      {isLoading ? (
+        <div className="loadingState">
+          <i className="fa-solid fa-gear"></i>
+        </div>
+      ) : (
+        <>
+          <Main />
+          {showModal && <SideBar handleToggleModal={handleToggleModal} />}
+          { data && (
+          <Footer handleToggleModal={handleToggleModal} />
+          )}
+          {error && <p>Error: {error}</p>}
+          <div dangerouslySetInnerHTML={{ __html: pageContent }} /> {/* Render HTML content */}
+        </>
+      )}
+    </>
+  );
+}
+
+export default App;

