+  position: absolute;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  object-fit: cover;
+  opacity: 0;
+  animation: slideshow 15s infinite;
+}
+
+.bgImage2 {
+  animation-delay: 5s;
+}
+
+.bgImage3 {
+  animation-delay: 10s;
+}
+
+@keyframes slideshow {
+  0%, 20% {
+    opacity: 0;
+  }
+  25%, 45% {
+    opacity: 1;
+  }
+  50%, 100% {
+    opacity: 0;
+  }
+}
+
+footer {
+  position: fixed;
+  bottom: 0;
+  left: 0;
+  width: 100%;
+  display: flex;
+  justify-content: space-between;
+  gap: 1rem;
+  padding: 1.5rem;
+}
+
+footer div {
+  display: flex;
+  flex-direction: column;
+  gap: 0.5rem;
+}
+
+footer h2 {
+  font-size: 1.6rem;
+}
+
+footer h1 {
+  font-size: 1.2rem;
+  font-weight: 200;
+}
+
+.footer-button {
+  background: transparent;
+  border: none;
+  outline: none;
+  color: aqua;
+  padding: 1.2rem;
+  transition-duration: 200ms;
+  cursor: pointer;
+  transition: transform 0.3s ease; /* Smooth transition for the movement */
+}
+
+.footer-button i {
+  font-size: 2rem;
+}
+
+footer button:hover {
+  opacity: 0.7;
+}
+
+/* Style when the sidebar is active */
+.sidebar-active .footer-button {
+  transform: translateX(-200px); /* Move the button to the right */
+}
+
+/* Media query for mobile devices */
+@media (max-width: 640px) {
+  footer {
+    padding: 0.5rem;
