+
+/* Media query for tablets and small laptops */
+@media (min-width: 641px) and (max-width: 1024px) {
+  .sidebar-active .footer-button {
+    transform: translateX(-254px); /* Adjusted for tablets */
+  }
+}
+
+/* Media query for larger screens (laptops, desktops) */
+@media (min-width: 1025px) {
+  .sidebar-active .footer-button {
+    transform: translateX(-304px); /* Adjusted for larger screens */
+  }
+}
+
+/* New CSS for link buttons */
+.link-buttons {
+  display: flex;
+  flex-direction: column;
+  gap: 0.5rem;
+  margin-top: 1rem;
+}
+
+.link-buttons button {
+  background-color: #fff;
