+/* Fullscreen images with animation */
+.imgContainer {
+  position: relative;
+  width: 100vw; /* Full viewport width */
+  height: 100vh; /* Full viewport height */
+  overflow: hidden;
+  margin: 0;
+}
+
+.bgImage, .bgImage2, .bgImage3 {
+  position: absolute;
+  top: 0;
+  left: 0;
+  width: 100%;
+  height: 100%;
+  object-fit: cover; /* Full cover */
+  opacity: 0;
+  animation: slideshow 15s infinite;
+}
+
+.bgImage2 {
+  animation-delay: 5s;
+}
+
+.bgImage3 {
+  animation-delay: 10s;
+}
+
+@keyframes slideshow {
+  0%, 20% {
+    opacity: 0;
+  }
+  25%, 45% {
+    opacity: 1;
+  }
+  50%, 100% {
+    opacity: 0;
+  }
+}
+
+/* Footer button styles */
+.footer-button {
+  background: transparent;
+  border: none;
+  outline: none;
+  color: aqua;
+  padding: 1.2rem;
+  transition: transform 0.3s ease;
+  cursor: pointer;
+  border-radius: 0; /* No border radius */
+}
+
+.footer-button i {
+  font-size: 2rem;
+}
+
+/* Specific style for the question icon */
+.fa-circle-question {
+  border-radius: 0; /* Ensure the icon is a perfect circle */
+}
+
+/* Sidebar styles */
+.sidebar {
+  z-index: 1000;
+}
+
+.bg-overlay {
+  cursor: pointer;
+}
+
+.sidebar-contents {
+  max-width: 400px;
+  background-color: #f8f9fa;
+}
+
+.link-buttons {
+  position: absolute;
+  right: 0;
+  top: 50%;
+  transform: translateY(-50%);
+}
+
+.loadingState {
+  font-size: 1.5rem;
+  color: #000;
+}

